import React, { useState, useEffect } from 'react';
import './App.css';
import SpotifyPlayer from 'spotify-player-sdk';

const App = () => {
  const [isConnectedToSpotify, setIsConnectedToSpotify] = useState(false);
  const [player, setPlayer] = useState(null);
  const [timeRemaining, setTimeRemaining] = useState(0);

  useEffect(() => {
    const createSpotifyPlayer = async () => {
      const newPlayer = new SpotifyPlayer({
        name: 'DevDance',
        getOAuthToken: async () => {
          // Get the access token from your server
          const response = await axios.get('/access_token');
          const accessToken = response.data.access_token;
          return accessToken;
        },
      });

      await newPlayer.connect();

      setPlayer(newPlayer);
    };

    createSpotifyPlayer();
  }, []);

  const startRegularPomodoro = () => {
    setTimeRemaining(25 * 60 * 1000);
  };

  const startExtendedPomodoro = () => {
    setTimeRemaining(45 * 60 * 1000);
  };

  const startBreak = () => {
    // Play a song from Spotify
    player.play('spotify:track:3Oi3M0tJXok66uW7C9WInR');
  };

  return (
    <div className="app">
      <header className="app-header">
        ðŸŽµ DevDance - Let's Groove While We Work! ðŸ’ƒ
      </header>
      <div className="pomodoro-container">
        <button className="pomodoro-button" onClick={startRegularPomodoro}>
          25 minutes + 5 min break
        </button>
        <button className="pomodoro-button" onClick={startExtendedPomodoro}>
          45 minutes + 15 min break
        </button>
        <button className="pomodoro-button" onClick={startBreak}>
          Start Break
        </button>
      </div>
    </div>
  );
};

export default App;
