import React from 'react';
import axios from 'axios';
import './CreateAccount.css';
import { API_BASE_URL } from './config'; // Import the API_BASE_URL from your config

class CreateAccount extends React.Component {
    constructor(props) {
        super(props);
        this.state = {
            username: '',
            email: '',
            password: '',
            confirmPassword: '',
            errorMessage: '',
            isAccountPageLoaded: false, // Add a flag to track if the account page is loaded
        };

        // Bind event handlers
        this.handleInputChange = this.handleInputChange.bind(this);
        this.handleSubmit = this.handleSubmit.bind(this);
    }

    // Add a new lifecycle method to load the account page
    async componentDidMount() {
        try {
            // Construct the full URL using the API_BASE_URL
            const accountCreationUrl = `${API_BASE_URL}/create-account`;

            // Send a GET request to retrieve the account creation page
            const response = await axios.get(accountCreationUrl); // Use the constructed URL
            // Update the state to indicate that the account page is loaded
            this.setState({ isAccountPageLoaded: true });
        } catch (error) {
            console.error('Error:', error);
            // Handle any errors here
        }
    }

    // Handle input field changes
    handleInputChange(event) {
        const { name, value } = event.target;
        this.setState({ [name]: value });
    }

    // Handle form submission
    async handleSubmit(event) {
        event.preventDefault();

        if (this.state.password !== this.state.confirmPassword) {
            this.setState({ errorMessage: 'Passwords do not match.' });
            return;
        }

        try {
            // Construct the full URL using the API_BASE_URL
            const createAccountUrl = `${API_BASE_URL}/create-account`;

            // Send a POST request to the /create-account route in the backend
            await axios.post(createAccountUrl, {
                username: this.state.username,
                email: this.state.email,
                password: this.state.password,
            });

            // Redirect to the login page or wherever you'd like after a successful registration
            this.props.history.push('/login');
        } catch (error) {
            this.setState({ errorMessage: 'Registration failed. Please try again.' });
        }
    }

    render() {
        // Check if the account page is loaded before rendering the form
        if (!this.state.isAccountPageLoaded) {
            return 'Loading...';
        }

        return `
            <div class="create-account-container">
                <h2>Create Account</h2>
                ${this.state.errorMessage ? `<p class="error">${this.state.errorMessage}</p>` : ''}
                <form onSubmit=${this.handleSubmit}>
                    <div class="input-group">
                        <label>Username:</label>
                        <input
                            type="text"
                            name="username"
                            value=${this.state.username}
                            onChange=${this.handleInputChange}
                            required
                        />
                    </div>
                    <div class="input-group">
                        <label>Email:</label>
                        <input
                            type="email"
                            name="email"
                            value=${this.state.email}
                            onChange=${this.handleInputChange}
                            required
                        />
                    </div>
                    <div class="input-group">
                        <label>Password:</label>
                        <input
                            type="password"
                            name="password"
                            value=${this.state.password}
                            onChange=${this.handleInputChange}
                            required
                        />
                    </div>
                    <div class="input-group">
                        <label>Confirm Password:</label>
                        <input
                            type="password"
                            name="confirmPassword"
                            value=${this.state.confirmPassword}
                            onChange=${this.handleInputChange}
                            required
                        />
                    </div>
                    <button type="submit">Create Account</button>
                </form>
            </div>
        `;
    }
}

export default CreateAccount;
