import React, { useState } from 'react';
import './App.css';

function App() {
  const [timerType, setTimerType] = useState(''); // to determine if it's a work or break timer

  const startPomodoro = () => {
    setTimerType('pomodoro');
    // logic for starting 25 minutes timer
  };

  const startShortBreak = () => {
    setTimerType('shortBreak');
    // logic for starting 5 minutes break
  };

  const startLongPomodoro = () => {
    setTimerType('longPomodoro');
    // logic for starting 45 minutes timer
  };

  const startLongBreak = () => {
    setTimerType('longBreak');
    // logic for starting 15 minutes break
  };

  const connectToSpotify = () => {
    window.location.href = 'http://localhost:8888/login'; // this is where the backend Spotify authorization happens
  };

  return (
    <div className="App">
      <header className="App-header">
        <h1>Devdance Pomodoro</h1>
        <button onClick={connectToSpotify}>Connect to Spotify</button>
        <div>
          <button onClick={startPomodoro}>Start Pomodoro (25 mins)</button>
          <button onClick={startShortBreak}>Short Break (5 mins)</button>
          <button onClick={startLongPomodoro}>Long Pomodoro (45 mins)</button>
          <button onClick={startLongBreak}>Long Break (15 mins)</button>
        </div>
      </header>
    </div>
  );
}

export default App;
