import React, { useState } from 'react';
import './App.css';

function App() {
  const [timerType, setTimerType] = useState('');

  const startRegularPomodoro = () => {
    setTimerType('regularPomodoro');
    // logic for starting 25 minutes timer and then 5-minute dance break
  };

  const startExtendedPomodoro = () => {
    setTimerType('extendedPomodoro');
    // logic for starting 45 minutes timer and then 15-minute dance break
  };

  const connectToSpotify = () => {
    window.location.href = 'http://localhost:8888/login';
  };

  return (
    <div className="App">
      <header className="App-header">
        <h1>Devdance Pomodoro</h1>
        <div className="button-group">
          <button className="pomodoro-button" onClick={startRegularPomodoro}>
            25 mins + Dance Break
          </button>
          <button className="pomodoro-button" onClick={startExtendedPomodoro}>
            45 mins + Extended Dance Break
          </button>
        </div>
        <button className="spotify-button" onClick={connectToSpotify}>
          Connect to Spotify
        </button>
      </header>
    </div>
  );
}

export default App;
