import React, { useState } from 'react';
import axios from 'axios';

function App() {
  const [accessToken, setAccessToken] = useState(null);

  function connectToSpotify() {
    window.location = `https://accounts.spotify.com/authorize?client_id=4d1fe896872e4fd18b145d6c7492af36&response_type=code&redirect_uri=http://localhost:3000/callback&scope=user-read-private user-read-email user-read-playback-state user-modify-playback-state`;
  }

  // ... (Your pomodoro logic and other component logic)

  const appElement = document.createElement('div');

  const headerElement = document.createElement('h1');
  headerElement.innerText = 'DevDance';
  appElement.appendChild(headerElement);

  const spotifyButton = document.createElement('button');
  spotifyButton.innerText = 'Connect to Spotify';
  spotifyButton.addEventListener('click', connectToSpotify);
  appElement.appendChild(spotifyButton);

  // Append other components and elements as needed

  return appElement;
}

export default App;
