import React from 'react';
import axios from 'axios';
import mainBackground from 'url(/mainbackground.png)';  // Adjust this path if needed based on the exact structure of your directories

class Login extends React.Component {
    constructor(props) {
        super(props);
        this.state = {
            email: '',
            password: '',
            errorMessage: '',
        };

        this.handleInputChange = this.handleInputChange.bind(this);
        this.handleSubmit = this.handleSubmit.bind(this);
    }

    handleInputChange(event) {
        const { name, value } = event.target;
        this.setState({ [name]: value });
    }

    async handleSubmit(event) {
        event.preventDefault();

        try {
            const response = await axios.post('/api/login', {
                email: this.state.email,
                password: this.state.password,
            });

            localStorage.setItem('token', response.data.token);
            // Redirect to homepage or wherever you want after a successful login
            this.props.history.push('/home');
        } catch (error) {
            this.setState({ errorMessage: 'Login failed. Please check your credentials.' });
        }
    }

    render() {
        return (
            <div className="login-container" style={{ backgroundImage: `url(${mainBackground})` }}>
                <h2>Login</h2>
                {this.state.errorMessage && <p className="error">{this.state.errorMessage}</p>}
                <form onSubmit={this.handleSubmit}>
                    <div className="input-group">
                        <label>Email:</label>
                        <input 
                            type="email" 
                            name="email" 
                            value={this.state.email}
                            onChange={this.handleInputChange}
                            required 
                        />
                    </div>
                    <div className="input-group">
                        <label>Password:</label>
                        <input 
                            type="password" 
                            name="password" 
                            value={this.state.password}
                            onChange={this.handleInputChange}
                            required 
                        />
                    </div>
                    <button type="submit">Login</button>
                </form>
            </div>
        );
    }
}

export default Login;
