import React, { useState, useEffect } from 'react';
import axios from 'axios'; // Ensure you install this package

function App() {
    const [timeLeft, setTimeLeft] = useState(null);
    const [playlist, setPlaylist] = useState(null);

    useEffect(() => {
        if (timeLeft > 0) {
            setTimeout(() => setTimeLeft(timeLeft - 1), 1000);
        } else if (timeLeft === 0) {
            // For demonstration, using a hardcoded playlist ID.
            axios.get('http://localhost:5000/get-playlist/YOUR_SPOTIFY_PLAYLIST_ID')
                .then(response => {
                    setPlaylist(response.data);
                }).catch(err => {
                    console.log('Error fetching playlist:', err);
                });
        }
    }, [timeLeft]);

    return (
        <div>
            <h1>DevDance</h1>
            <button onClick={() => setTimeLeft(1500)}>Start 25min Timer</button>
            <button onClick={() => setTimeLeft(2700)}>Start 45min Timer</button>
            {timeLeft && <p>Time Left: {Math.floor(timeLeft / 60)}:{timeLeft % 60}</p>}
            {playlist && (
                <div>
                    <h2>Dance Break!</h2>
                    {/* For demonstration, showing the first song's name */}
                    <p>Song: {playlist.items[0].track.name}</p>
                </div>
            )}
        </div>
    );
}

export default App;
