import React, { useState } from 'react';
import './App.css';

const App = () => {
    const [timerType, setTimerType] = useState(null);

    const startRegularPomodoro = () => {
        setTimerType('regularPomodoro');
        // Start the 25 minutes timer
        setTimeout(() => {
            alert('Time for a 5-minute dance break!');
            // You'd probably replace this alert with logic to start Spotify music or any other action you want.
        }, 25 * 60 * 1000);  // 25 minutes
    };

    const startExtendedPomodoro = () => {
        setTimerType('extendedPomodoro');
        // Start the 45 minutes timer
        setTimeout(() => {
            alert('Time for a 15-minute dance break!');
            // Same here: replace this alert with appropriate action.
        }, 45 * 60 * 1000);  // 45 minutes
    };

    return (
        <div className="App">
            <header className="App-header">
                <h1>DevDance: Dance to the Pomodoro Beat! ðŸŽµ</h1>
                <div className="button-group">
                    <button className="pomodoro-button" onClick={startRegularPomodoro}>
                        25 minutes + 5 min break
                    </button>
                    <button className="pomodoro-button" onClick={startExtendedPomodoro}>
                        45 minutes + 15 min break
                    </button>
                </div>
                <div className="spotify-connect">
                    <button className="spotify-button">
                        Connect to Spotify
                    </button>
                </div>
            </header>
        </div>
    );
}

export default App;
