import React, { useState, useEffect } from 'react';
import { Link, useNavigate } from 'react-router-dom';
import "./TimerComponent.css";

function TimerComponent() {
  const navigate = useNavigate();
  const [timerDuration, setTimerDuration] = useState(0);

  const startTimer = (duration) => {
    setTimerDuration(duration);
    // Start your countdown logic here
  };

  useEffect(() => {
    let timerInterval;

    if (timerDuration > 0) {
      timerInterval = setInterval(() => {
        setTimerDuration((prevDuration) => prevDuration - 1);
      }, 1000);
    } else if (timerDuration === 0) {
      clearInterval(timerInterval);
      // Timer has reached zero, navigate to the break page
      navigate(`/break?duration=${timerDuration === 25 ? 5 : 15}`);
    }

    return () => clearInterval(timerInterval);
  }, [timerDuration, navigate]);

  return (
    <div>
      <h2>Timer</h2>
      <button onClick={() => startTimer(25)}>Start 25-Minute Timer</button>
      <button onClick={() => startTimer(45)}>Start 45-Minute Timer</button>
      {timerDuration > 0 ? <p>Timer is running...</p> : null}
      {timerDuration === 0 ? <p>Timer is not running.</p> : null}
      <Link to={`/break?duration=${timerDuration === 25 ? 5 : 15}`}>Go to Break</Link>
    </div>
  );
}

export default TimerComponent;
